/* Стили для модального окна */
.modal {
  display: none; /* Скрываем модальное окно по умолчанию */
  position: fixed;
  z-index: 999;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.7); /* Полупрозрачный фон */
  overflow: hidden;
}
.modal-img {
  width: 200px;
  height: 200px;
  margin-bottom: 20px;
}

.modal-content {
  background-color: #fefefe;
  margin: 15% auto;
  padding: 20px;
  border: 1px solid #888;
  width: 80%;
  max-width: 800px;

  text-align: center;
  position: relative;

  display: flex;
  flex-direction: row;
  justify-content: space-around;
}
.modal__text {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
}
.close {
  position: absolute;
  top: 10px;
  right: 20px;
  font-size: 24px;
  font-weight: bold;
  cursor: pointer;
}

/* Стили для изображения продукта */
.product-img {
  width: 200px;
  height: 200px;
  cursor: pointer;
}

/* Стили для контейнера с изображением продукта */
.product-container {
  text-align: center;
}

/* Анимация для модального окна */
.modal-content {
  animation: modal-open 0.3s ease;
}
.popup__close {
  background-color: transparent;
  border: none;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 30px;
  height: 30px;
  padding: 0;
  margin: 0;
  position: absolute;
  top: 10px;
  right: 10px;
}
.popup__close:focus {
  outline: none;
}

.close-icon {
  font-size: 40px;
  font-weight: bold;
  color: #666;
  display: block;
  line-height: 1;
  transition: color 0.3s ease;
}

.popup__close:hover .close-icon,
.popup__close:focus .close-icon {
  color: #333;
}

@keyframes modal-open {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

